const Section = styled.section`
  padding-block: var(--padding-section);
`;

const IframesWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;

  & > iframe {
    border: var(--border) solid rgb(var(--fg));
    border-radius: var(--border-radius);
    aspect-ratio: 16/9;
    height: auto;
    width: 100%;
    flex-shrink: 2;
  }

  @media (max-width: 1100px) {
    flex-direction: column;
  }

  & > svg {
    margin: 0.25rem;
    flex-grow: 2;
    height: 2rem;

    &.h {
      display: none;
    }

    @media (min-width: 1100px) {
      &.v {
        display: none;
      }
      &.h {
        display: block;
        width: 3rem;
        height: auto;
      }
    }
  }
`;

const VideosWrapper = styled.div`
  position: relative;

  & > svg {
    width: 7rem;
  }

  & > svg:first-of-type {
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
    transform: translate(-30%, -50%);
  }

  & > svg:nth-of-type(2) {
    position: absolute;
    bottom: 0;
    left: 0;
    z-index: -1;
    transform: rotateX(180deg) translate(-30%, -50%);
  }

  & > svg:nth-of-type(3) {
    position: absolute;
    top: 0;
    right: 0;
    z-index: -1;
    transform: rotateY(180deg) translate(-30%, -50%);
  }

  & > svg:nth-of-type(4) {
    position: absolute;
    bottom: 0;
    right: 0;
    z-index: -1;
    transform: rotate(180deg) translate(-30%, -50%);
  }
`;

const Svg = styled.svg`
  & > path {
    fill: rgb(var(--fg));
  }
`;

export default Videos;
